export const displayBrowserNotification = (
    title: string,
    body: string,
    icon: string = "https://laravel.com/img/notification-icon.png",
    timestamp: string,
    functionClick: () => void
) => {
    if (Notification.permission === "granted") {
        const notification = new Notification(title, {
            body: body,
            icon: icon,
            tag: "notification-tag",
            requireInteraction: true,
            timestamp: timestamp,
        });
        playNotificationSound();

        // Add click handler
        notification.addEventListener("click", function () {
            // Focus the window
            window.focus();

            // Navigate to the URL
            functionClick();
            // Close notification after click
            notification.close();
        });
    } else {
        console.warn("Notification permission not granted.");

        // Optional: request permission
        Notification.requestPermission().then((permission) => {
            if (permission === "granted") {
                console.log("Notification permission granted.");
            }
        });
    }
}

const NOTIFICATION_SOUND_BASE64 = "data:audio/wav;base64,";

// Option 2: Alternative notification sound (slightly different tone)
const NOTIFICATION_SOUND_ALT = "data:audio/wav;base64,UklGRiQAAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YQAAAAA=";

// Option 3: Triple beep notification (more noticeable)
const NOTIFICATION_SOUND_TRIPLE = "data:audio/wav;base64,";



const playNotificationSound = () => {
    const audio = new Audio(NOTIFICATION_SOUND_TRIPLE);
    audio.play();
}